class NewsResponse {
  NewsResponse(
      {this.status, this.totalResults, this.newsList, this.code, this.message});

  NewsResponse.fromJson(dynamic json) {
    status = json['status'];
    code = json['code'];
    message = json['message'];
    totalResults = json['totalResults'];
    if (json['articles'] != null) {
      newsList = [];
      json['articles'].forEach((v) {
        newsList?.add(News.fromJson(v));
      });
    }
  }

  String? status, code, message;
  num? totalResults;
  List<News>? newsList;

  NewsResponse copyWith({
    String? status,
    num? totalResults,
    List<News>? articles,
  }) =>
      NewsResponse(
        status: status ?? this.status,
        totalResults: totalResults ?? this.totalResults,
        newsList: articles ?? this.newsList,
      );

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['status'] = status;
    map['totalResults'] = totalResults;
    if (newsList != null) {
      map['articles'] = newsList?.map((v) => v.toJson()).toList();
    }
    return map;
  }
}

